    private string _dialogTop;
    private string _dialogDefaultTop => Centered ? "25%;" : "5%;";
    private string GetDialogTop()
    {
        var height = Height;
        if (MinHeight > height)
        {
            height = MinHeight;
        }

        return Centered ? "50%;" : $"calc({(Height == 0 ? 15 : 2)}% + {(int)(height / 2)}px);";
